using namespace std;

class Solution {
    public:
        int longestValidParentheses(string s) {
            int max = 0;
            int curmax = 0;

            stack<char> S;
            vector<int> d(s.length(), 0);
            stack<int> Si;


            for (int i = 0; i < s.size(); i ++){
                char c = s[i];
                switch (c) {
                    case '(':
                        d[i] = 0;
                        S.push(c);
                        Si.push(i);
                        curmax = 0;
                        break;
                    case ')':
                        if (S.empty() || S.top() != '(') {
                            max = (max>curmax?max:curmax);
                            curmax = 0;
                            d[i] = -1;
                            S.push(c);
                            Si.push(i);
                        } else {
                            int k = Si.top();
                            curmax += 2;
                            if (k > 0 && d[k-1] != -1)
                                curmax += d[k-1];
                            d[i] = curmax;
                            max = (max>curmax?max:curmax);
                            S.pop();
                            Si.pop();
                        }
                        break;
                }
            }
            
            return max;
        }
};
